mapper.select:
select o.order_id, o.order_number, o.order_price, c.customer_name, o.order_state, o.order_date, o.receive_tel, o.order_address, o.receive_contact, o.order_remark, o.created_by, o.gmt_create, o.last_modified_by, o.gmt_modified, o.is_deleted, o.sort_no, o.version from t_order as o left join t_customer as c ON c.customer_id = o.customer_id where o.is_deleted = 0
and o.order_number = #{orderNumber}
and o.order_state = #{orderState}
and o.order_date >= #{orderDateStart}
and o.order_date <= #{orderDateEnd}
order by o.gmt_create desc

mapper.update:
UPDATE t_order
SET order_state = 2,
    version = version + 1,
    gmt_modified = now(),
    last_modified_by = #{lastModifiedBy}

                #{orderId}
